import Wheader from "./Wheader"
import weather from "../db/weather.json"
import weather2 from "../db/weather2.json"
import { Link, useNavigate } from "react-router-dom";


const Wmenu = () => {

    const items1  = weather.response.body.items.item ;
    const items2  = weather2.response.body.items.item ;

    const navigate=useNavigate();
    const url = {
        '단기예보' : '/W1?item=단기예보' ,
        '중기예보' : '/W2?item=중기예보',
        
    }

    const goUrl = (item) => {
        navigate(url[item]);
    }

    return (
        <>  
            <Wheader title={"일기예보"}/>
            <div className="menubutton" onClick={()=>goUrl('단기예보')}>단기예보</div>
            <button className="menubutton" onClick={()=>goUrl('중기예보')}>중기예보</button>
            
        </>
    );
    
    }
    export default Wmenu;
