import { useState,useEffect } from "react";

const Boxofficedetail = (mvcd) => {

    const [mtag,setMtag] =useState();

    console.log("박스오피스 디테일",mvcd)

    const getData = async() => {
        let url=''
        url = url + `movieCd=${mvcd}` ;
        try {

            const resp = await fetch(url);
            const data = await resp.json();

            let temp = data.movieInfoResult.movieInfo;
            let keys = {'movieNm' : '영화명',
                        
                        'prdtstatNm' : '개봉일',}
            for(let [k,v] of Object.entries(temp)) {
                console.log(k,v)
            }

            temp.map((i)=>console.log(i))

            setMtag(temp);
            console.log("temp",temp);
        }
        catch(err) {
            console.log(err)
        }

    }

    useEffect(()=>{
        getData();
    },[mvcd]);
    return (
        <>
            {mvcd && mtag}
        </>
    );
    
    }
    export default Boxofficedetail;
