import { useState } from "react";

const Mycom = () => {
    
    let cnt1 = 0;

    const [cnt2, setCnt2] = useState(0);

    const addCnt1 = () => {
        cnt1 = cnt1 + 1 ;
        console.log(cnt1) ;
    }

    const addCnt2 = () => {
        cnt2 = cnt2
    }

    return (
       <>
            <ul>
                <li>cnt1 = {cnt1}</li>
                <li>cnt2 = {cnt2}</li>
            </ul>
            <form>
                <input type="button" value="cnt1증가" onClick={addCnt1}> </input>
                <input type="button" value="cnt2증가"> </input>
            </form>
       </>
    );
}

export default Mycom;
