import { useState,useEffect } from "react";


const BoxofficeList = ({tDt}) => {
    //console.log("BoxofficeList",tDt)

    const [mvlist, setMvlist]=useState();
    
    useEffect(()=>{
        setMvlist(weather2.response.body.items.item);
    },[]);

    useEffect(()=>{
        const apikey='f5eef3421c602c6cb7ea224104795888';
        let url =`http://kobis.or.kr/kobisopenapi/webservice/rest/boxoffice/searchDailyBoxOfficeList.json?key=${apikey}&targetDt=${tDt}`;

        fetch(url)
        .then((resp)=>resp.json())
        .then((data)=>{
            
            let temp = data.boxOfficeResult.dailyBoxOfficeList;
            console.log(temp)

           temp.map((item)=>{
                return (
                    <div>
                        <span className="sp0">{item.rank}</span>
                        <span className="sp1">{item.movieNm}</span>
                        <span className="sp2">{item.showCnt}</span>
                    </div>
                )
            })
            setMvlist(temp);
        })
        .catch((err)=>console.log(err))
    }, [tDt]);

    return (
        <div>
            <div>
                {tDt && mvlist}
            </div>
            <div>
                상세
            </div>
        </div>
    );
    
    }
    export default BoxofficeList;
