import { useParams } from "react-router-dom";

const RouterPage1 = () => {
    
    const item = useParams().item ;
    console.log("p1 item" , item)

    const abk = () => {
        if (item = "사과🍎") {
            <p>사과는 과일입니다 🍎</p>
        } else if ( item = "바나나🍌") {
            <p>바나나는 과일입니다 🍌</p>
        } else if ( item = "당근🥕"){
            <p>당근은 과일이 아닙니다.</p>
        }
         
    }

    
    return (
        <>
         <h1>Page1</h1>
        {abk}
        </>
    );
    
    }
    export default RouterPage1;
