import Wheader from "./Wheader"
import item from "../db/item.json"
import weather2 from "../db/weather2.json"

const W1 = () => {
    //console.log(weather2)
    //console.log(item)
    let w2item = weather2.response.body.items.item ;
    let w2  = w2item.map((i)=>{
        let temp = [] ;
        temp.push(item[i.category][0]);
        temp.push(i.obsrValue);
        temp.push(item[i.category][1]);
        return temp;

        
    } );

    console.log(w2);
    return (
        <>
            <Wheader title={"일기예보 - 단기정보"}/>
            <div className="Wcard"> 
                <div><span>강수량</span><span>0</span><span>mm</span></div>
            </div>
        </>
    );
    
    }
    export default W1;
